(162, 'LIỀN KỀ VINHOMES THE HARMONY', '', '<h1 style=\"text-align:justify\"><span style=\"color:#000000\">LIỀN KỀ VINHOMES THE HARMONY</span></h1>\r\n\r\n<p style=\"text-align:justify\"><strong><a href=\"http://vinhometheharmony.com/\"><span style=\"color:#000000\">Liền kề Vinhomes Harmony</span></a><span style=\"color:#000000\">&nbsp;</span></strong><span style=\"color:#000000\"><strong>&nbsp;</strong>tại dự &aacute;n c&oacute; thiết kế kiến tr&uacute;c&nbsp;hiện đại với s&acirc;n trước vườn sau tho&aacute;ng đ&atilde;ng. C&aacute;c l&ocirc; c&oacute; diện t&iacute;ch đất chủ yếutừ&nbsp;<strong>90 &ndash; 96m2</strong>, ngo&agrave;i ra c&oacute; th&ecirc;m một số căn diện t&iacute;ch đất&nbsp;<strong>120 &ndash; 140m2</strong>. Diện t&iacute;ch x&acirc;y dựng&nbsp;<strong>220m2.</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#000000\"><strong>1. Khu Phong Lan</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#000000\"><a href=\"http://vinhometheharmony.com/wp-content/uploads/2017/03/lien-ke-phap-e1488789888131.jpg\"><img alt=\"lien-ke-phap\" height=\"707\" src=\"http://vinhometheharmony.com/wp-content/uploads/2017/03/lien-ke-phap-e1488789888131.jpg\" width=\"1000\" /></a></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#000000\"><strong>2. Khu Nguyệt Quế</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#000000\"><a href=\"http://vinhometheharmony.com/wp-content/uploads/2017/03/lien-ke-hy-lap-e1488789765688.jpg\"><img alt=\"lien-ke-hy-lap\" height=\"707\" src=\"http://vinhometheharmony.com/wp-content/uploads/2017/03/lien-ke-hy-lap-e1488789765688.jpg\" width=\"1000\" /></a></span></p>\r\n', 'lien-ke-phap-e1488789888131.jpg', 0, 71, '', '2018-10-30 15:10:39', NULL, 1, '', '', '', '', '', 'LIỀN KỀ VINHOMES THE HARMONY', '', 'lien-ke-vinhomes-the-harmony', '', 1, 0),
(163, 'SHOPHOUSE VINHOMES THE HARMONY', '', '<h1 style=\"text-align:justify\"><span style=\"color:#000000\">SHOPHOUSE VINHOMES THE HARMONY</span></h1>\r\n\r\n<p style=\"text-align:justify\"><a href=\"http://vinhometheharmony.com/\"><span style=\"color:#000000\"><strong>Shophouse Vinhomes Harmony</strong></span></a><span style=\"color:#000000\">&nbsp;tại dự dự &aacute;n được x&acirc;y dựng theo &yacute; tưởng nh&agrave; phố truyền thống của Việt Nam với mục đ&iacute;ch kinh doanh tại tầng 1, c&aacute;c tầng tr&ecirc;n để ở. Diện t&iacute;ch đất c&aacute;c l&ocirc;&nbsp;<strong>Shophouse The Harmony</strong>&nbsp;từ 170m2-200m2, diện t&iacute;ch x&acirc;y dựng 400m2-440m2, c&oacute; vị tr&iacute; trung t&acirc;m rất ph&ugrave; hợp cho việc kinh doanh, bu&ocirc;n b&aacute;n.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#000000\"><strong>1. Khu Phong Lan</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#000000\"><a href=\"http://vinhometheharmony.com/wp-content/uploads/2017/03/shophouse-phap-e1488789925482.jpg\"><img alt=\"shophouse-phap\" height=\"707\" src=\"http://vinhometheharmony.com/wp-content/uploads/2017/03/shophouse-phap-e1488789925482.jpg\" width=\"1000\" /></a></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#000000\"><strong>2. Khu Hướng Dương</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#000000\"><a href=\"http://vinhometheharmony.com/wp-content/uploads/2017/03/shophouse-dong-duong-e1488789685752.jpg\"><img alt=\"shophouse-dong-duong\" height=\"707\" src=\"http://vinhometheharmony.com/wp-content/uploads/2017/03/shophouse-dong-duong-e1488789685752.jpg\" width=\"1000\" /></a></span></p>\r\n', 'shophouse-dong-duong-e1488789685752.jpg', 0, 71, '', '2018-10-30 15:10:08', '2018-11-07 10:11:58', 1, '', '', '', '', '', 'SHOPHOUSE VINHOMES THE HARMONY', '', 'shophouse-vinhomes-the-harmony', '', 1, 0),
