INSERT INTO `news_languages` (`id`, `news_id`, `languages_code`, `lang_news_name`, `lang_news_des`, `lang_news_content`, `lang_news_sub_info1`, `lang_news_sub_info2`, `lang_news_sub_info3`, `lang_news_sub_info4`, `lang_news_sub_info5`, `edit_state`, `friendly_url`, `title_seo`, `des_seo`, `keyword`) VALUES
(229, 115, 'vn', '9 dự án quy mô hơn 200.000 tỷ đồng của Vingroup đang triển khai đến đâu?', 'Tập đoàn Vingroup vừa thông báo tiến độ 9 dự án trọng điểm tới cổ đông. 9 dự án này có tổng quy mô hơn 200.000 tỷ đồng, trong đó 3 dự án lớn nhất là Vincity Gia Lâm (100.000 tỷ), Vinhomes Central Park (37.712 tỷ) và Vinfast (35.000 tỷ).', '<p style=\"text-align:justify\"><a href=\"javascript: void(0)\"><img alt=\"\" height=\"499\" src=\"http://bdsphucloc.vn/wp-content/uploads/2018/07/4-1.jpg\" width=\"750\" /></a></p>\r\n\r\n<p style=\"text-align:justify\"><strong>Tập đo&agrave;n Vingroup vừa th&ocirc;ng b&aacute;o tiến độ 9 dự &aacute;n trọng điểm tới cổ đ&ocirc;ng. 9 dự &aacute;n n&agrave;y c&oacute; tổng quy m&ocirc; hơn 200.000 tỷ đồng, trong đ&oacute; 3 dự &aacute;n lớn nhất l&agrave; Vincity Gia L&acirc;m (100.000 tỷ), Vinhomes Central Park (37.712 tỷ) v&agrave; Vinfast (35.000 tỷ).</strong></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><strong>1. Vinhomes Central Park &ndash; Quận B&igrave;nh Thạnh, TPHCM (37.712 tỷ đồng)</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Dự &aacute;n c&oacute; tổng diện t&iacute;ch đất khoảng hơn 40ha, l&agrave; khu đ&ocirc; thị đa chức năng với hệ thống hạ tầng kỹ thuật v&agrave; hạ tầng x&atilde; hội đồng bộ. Dự &aacute;n c&oacute; 18 t&ograve;a nh&agrave; ở cao tầng, 100 căn biệt thự, 1 bệnh viện đa khoa, 1 trường học li&ecirc;n cấp.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" height=\"255\" src=\"http://bdsphucloc.vn/wp-content/uploads/2018/07/1.jpg\" width=\"750\" /></p>\r\n\r\n<p style=\"text-align:justify\">Hiện nay, dự &aacute;n đ&atilde; được đưa v&agrave;o sử dụng, trừ t&ograve;a Landmark 81 đang ho&agrave;n thiện. Tỷ lệ lợi &iacute;ch của Tập đo&agrave;n Vingroup tại dự &aacute;n l&agrave; 95%.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><strong>2. Vinhomes The Harmony &ndash; Quận Long Bi&ecirc;n, H&agrave; Nội (4.877 tỷ đồng)</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Đ&acirc;y l&agrave; dự &aacute;n nằm s&aacute;t với khu đ&ocirc; thị sinh th&aacute;i Vinhomes Riverside, nơi Tập đo&agrave;n Vingroup đặt trụ sở ch&iacute;nh. Dự &aacute;n c&oacute; tổng diện t&iacute;ch đất khoảng 97ha, c&oacute; c&aacute;c khu biệt thự, nh&agrave; vườn, trường học v&agrave; do Tập đo&agrave;n Vingroup sở hữu 100% tỷ lệ lợi &iacute;ch.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" height=\"383\" src=\"http://bdsphucloc.vn/wp-content/uploads/2018/07/2-1.jpg\" width=\"750\" /></p>\r\n\r\n<p style=\"text-align:justify\">Hiện nay, dự &aacute;n đ&atilde; thi c&ocirc;ng xong 90% c&aacute;c hạng mục hạ tầng kỹ thuật v&agrave; ho&agrave;n th&agrave;nh 75% c&ocirc;ng việc thi c&ocirc;ng c&aacute;c khu nh&agrave; thấp tầng. Ngo&agrave;i ra, c&aacute;c khu đất trường học cơ bản ho&agrave;n th&agrave;nh, dự kiến tuyển sinh trong năm học n&agrave;y.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><strong>3. Vinhomes Green Bay &ndash; Quận Nam Từ Li&ecirc;m, H&agrave; Nội (4.992 tỷ đồng)</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Dự &aacute;n bao gồm nh&agrave; cao tầng, khu biệt thự, nh&agrave; liền kề, trường học, đặt tr&ecirc;n khu đất c&oacute; diện t&iacute;ch khoảng 30ha, do Vingroup sở hữu 98,86%.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" height=\"422\" src=\"http://bdsphucloc.vn/wp-content/uploads/2018/07/3.png\" width=\"750\" /></p>\r\n\r\n<p style=\"text-align:justify\">Phần hạ tầng to&agrave;n dự &aacute;n đang thi c&ocirc;ng đạt 70% khối lượng c&ocirc;ng việc. Khu thấp tầng đ&atilde; ho&agrave;n th&agrave;nh 100% c&ograve;n khu cao tầng đang ho&agrave;n th&agrave;nh thi c&ocirc;ng th&ocirc;, ho&agrave;n thiện v&agrave; lắp đặt nội thất c&aacute;c căn hộ, dự kiến ho&agrave;n th&agrave;nh trong qu&yacute; 1/2019.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><strong>4. Vinhomes Skylake &ndash; Quận Nam Từ Li&ecirc;m, H&agrave; Nội (3.786 tỷ đồng)</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Đ&acirc;y l&agrave; dự &aacute;n chung cư cao tầng, tổng diện t&iacute;ch đất khoảng 2,3ha do Vingroup nắm 94% tỷ lệ lợi &iacute;ch.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" height=\"499\" src=\"http://bdsphucloc.vn/wp-content/uploads/2018/07/4-1.jpg\" width=\"750\" /></p>\r\n\r\n<p style=\"text-align:justify\">Hiện nay dự &aacute;n đang thi c&ocirc;ng phần cao tầng, trong đ&oacute; 1 t&ograve;a đ&atilde; cất n&oacute;c, 2 t&ograve;a c&ograve;n lại dự kiến cất n&oacute;c v&agrave;o cuối th&aacute;ng 5/2018.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><strong>5. VinCity Gia L&acirc;m &ndash; Huyện Gia L&acirc;m, H&agrave; Nội (dự kiến 100.000 tỷ đồng)</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Theo nghi&ecirc;n cứu quy hoạch, quy m&ocirc; dự &aacute;n khoảng hơn 400ha, bao gồm rất nhiều hạng mục như chung cư cao tầng, nh&agrave; ở thấp tầng, trường học c&aacute;c cấp, trường đại học, bệnh viện v&agrave; c&aacute;c tiện &iacute;ch đ&ocirc; thị. Dự &aacute;n nhằm mục đ&iacute;ch thu h&uacute;t lượng lớn cư d&acirc;n về sinh sống, giảm tải cho nội đ&ocirc; H&agrave; Nội v&agrave; c&aacute;c v&ugrave;ng l&acirc;n cận.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" height=\"466\" src=\"http://bdsphucloc.vn/wp-content/uploads/2018/07/5-1.jpg\" width=\"750\" /></p>\r\n\r\n<p style=\"text-align:justify\">Hiện nay, dự &aacute;n đ&atilde; được Thủ tướng Ch&iacute;nh phủ v&agrave; UBND th&agrave;nh phố chấp thuận điều chỉnh cục bộ Quy hoạch chung x&acirc;y dựng Thủ đ&ocirc; l&agrave;m cơ sở triển khai v&agrave; đang tiến h&agrave;nh thẩm định QHCT 1/500. Vingroup nắm 84% tỷ lệ lợi &iacute;ch của dự &aacute;n n&agrave;y.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><strong>6. Vinhomes Metropolis &ndash; Quận Ba Đ&igrave;nh, H&agrave; Nội (4.876 tỷ đồng)</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Đ&acirc;y l&agrave; dự &aacute;n văn ph&ograve;ng, chung cư cao tầng, trường học c&oacute; quy m&ocirc; khoảng 3,5ha, do Vingroup nắm 49,41% tỷ lệ lợi &iacute;ch.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" height=\"448\" src=\"http://bdsphucloc.vn/wp-content/uploads/2018/07/6-1.jpg\" width=\"750\" /></p>\r\n\r\n<p style=\"text-align:justify\">Khu hỗn hợp của dự &aacute;n đ&atilde; ho&agrave;n th&agrave;nh phần th&ocirc;, đang thi c&ocirc;ng ho&agrave;n thiện đến tầng 20, dự kiến ho&agrave;n th&agrave;nh cuối năm 2018. Khu văn ph&ograve;ng vừa được khởi c&ocirc;ng đầu năm nay, dự kiến sẽ ho&agrave;n th&agrave;nh năm 2020.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><strong>7. Vinhomes Imperia Hải Ph&ograve;ng &ndash; Quận Hồng B&agrave;ng, Hải Ph&ograve;ng (4.950 tỷ đồng)</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Đ&acirc;y l&agrave; khu đ&ocirc; thị c&oacute; tổng diện t&iacute;ch khoảng 78ha, đặt tại cửa ng&otilde; ph&iacute;a T&acirc;y của th&agrave;nh phố Hải Ph&ograve;ng. Dự &aacute;n gồm nh&agrave; ở thấp tầng, trường học, văn ph&ograve;ng, kh&aacute;ch sạn, trung t&acirc;m thương mại v&agrave; do Vingroup sở hữu 100%.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" height=\"379\" src=\"http://bdsphucloc.vn/wp-content/uploads/2018/07/7-1.jpg\" width=\"750\" /></p>\r\n\r\n<p style=\"text-align:justify\">Hiện nay, khu nh&agrave; ở thấp tầng, khu Trung t&acirc;m thương mại, kh&aacute;ch sạn v&agrave; văn ph&ograve;ng đang được thi c&ocirc;ng. C&aacute;c trường học đ&atilde; thi c&ocirc;ng xong v&agrave; đang chuẩn bị nghiệm thu b&agrave;n giao.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><strong>8. Vinhomes Star City Thanh H&oacute;a &ndash; Phường Đ&ocirc;ng Hải, Thanh H&oacute;a (11.623 tỷ đồng)</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Dự &aacute;n c&oacute; quy m&ocirc; khoảng 118ha, l&agrave; khu đ&ocirc; thị mới đồng bộ ho&agrave;n chỉnh về hạ tầng kỹ thuật, hạ tầng x&atilde; hội (khu nh&agrave; ở, dịch vụ thương mại, y tế, san nền, giao th&ocirc;ng, hệ thống cấp điện, cấp tho&aacute;t nước, c&acirc;y xanh)&hellip; đ&aacute;p ứng nhu cầu về nh&agrave; ở, dịch vụ thương mại cho người d&acirc;n đồng thời tạo việc l&agrave;m cho người lao động.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" height=\"243\" src=\"http://bdsphucloc.vn/wp-content/uploads/2018/07/8-1.jpg\" width=\"750\" /></p>\r\n\r\n<p style=\"text-align:justify\">Dự &aacute;n n&agrave;y đang triển khai x&acirc;y dựng, san lấp mặt bằng thi c&ocirc;ng phần hạ tầng v&agrave; do Vingroup nắm 100% tỷ lệ lợi &iacute;ch.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><strong>9. Nh&agrave; m&aacute;y sản xuất &ocirc; t&ocirc; VinFast &ndash; Hải Ph&ograve;ng (35.000 tỷ đồng)</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Dự &aacute;n c&oacute; quy m&ocirc; 335ha n&agrave;y được đặt tại Đảo C&aacute;t hải, huyện C&aacute;t Hải, thuộc khu kinh tế Đ&igrave;nh Vũ &ndash; C&aacute;t Hải, th&agrave;nh phố Hải Ph&ograve;ng. Vingroup c&oacute; tham vọng đưa VinFast trở th&agrave;nh thương hiệu quốc gia, khẳng định vị thế của Việt Nam tr&ecirc;n bản đồ sản xuất &ocirc; t&ocirc; thế giới với c&ocirc;ng suất thiết kế giai đoạn 1 đạt 250.000 xe m&aacute;y điện/năm v&agrave; 250.000 &ocirc; t&ocirc;/năm.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" height=\"360\" src=\"http://bdsphucloc.vn/wp-content/uploads/2018/07/9.png\" width=\"750\" /></p>\r\n\r\n<p style=\"text-align:justify\">Dự &aacute;n c&oacute; 2 hạng mục ch&iacute;nh l&agrave; khu sản xuất xe m&aacute;y E-Scooter, hiện đ&atilde; thi c&ocirc;ng được khoảng 95% v&agrave; phần sản xuất &ocirc; t&ocirc;, hiện đ&atilde; thi c&ocirc;ng được khoảng 25%. Vingroup sở hữu 100% dự &aacute;n n&agrave;y.</p>\r\n', '', '', '', '', '', 0, '9-du-an-quy-mo-hon-200000-ty-dong-cua-vingroup-dang-trien-khai-den-dau', '9 dự án quy mô hơn 200.000 tỷ đồng của Vingroup đang triển khai đến đâu?', '', '9 dự án của vingroup'),
(230, 115, 'en', 'english version 9 dự án quy mô hơn 200.000 tỷ đồng của Vingroup đang triển khai đến đâu?', 'english version Tập đoàn Vingroup vừa thông báo tiến độ 9 dự án trọng điểm tới cổ đông. 9 dự án này có tổng quy mô hơn 200.000 tỷ đồng, trong đó 3 dự án lớn nhất là Vincity Gia Lâm (100.000 tỷ), Vinhomes Central Park (37.712 tỷ) và Vinfast (35.000 tỷ).', 'english version <p><a href=\"javascript: void(0)\"><img alt=\"\" height=\"426\" src=\"http://bdsphucloc.vn/wp-content/uploads/2018/07/4-1.jpg\" width=\"640\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Tập đo&agrave;n Vingroup vừa th&ocirc;ng b&aacute;o tiến độ 9 dự &aacute;n trọng điểm tới cổ đ&ocirc;ng. 9 dự &aacute;n n&agrave;y c&oacute; tổng quy m&ocirc; hơn 200.000 tỷ đồng, trong đ&oacute; 3 dự &aacute;n lớn nhất l&agrave; Vincity Gia L&acirc;m (100.000 tỷ), Vinhomes Central Park (37.712 tỷ) v&agrave; Vinfast (35.000 tỷ).</strong></p>\r\n\r\n<p><strong>1. Vinhomes Central Park &ndash; Quận B&igrave;nh Thạnh, TPHCM (37.712 tỷ đồng)</strong></p>\r\n\r\n<p>Dự &aacute;n c&oacute; tổng diện t&iacute;ch đất khoảng hơn 40ha, l&agrave; khu đ&ocirc; thị đa chức năng với hệ thống hạ tầng kỹ thuật v&agrave; hạ tầng x&atilde; hội đồng bộ. Dự &aacute;n c&oacute; 18 t&ograve;a nh&agrave; ở cao tầng, 100 căn biệt thự, 1 bệnh viện đa khoa, 1 trường học li&ecirc;n cấp.</p>\r\n\r\n<p><img alt=\"\" height=\"218\" src=\"http://bdsphucloc.vn/wp-content/uploads/2018/07/1.jpg\" width=\"640\" /></p>\r\n\r\n<p>Hiện nay, dự &aacute;n đ&atilde; được đưa v&agrave;o sử dụng, trừ t&ograve;a Landmark 81 đang ho&agrave;n thiện. Tỷ lệ lợi &iacute;ch của Tập đo&agrave;n Vingroup tại dự &aacute;n l&agrave; 95%.</p>\r\n\r\n<p><strong>2. Vinhomes The Harmony &ndash; Quận Long Bi&ecirc;n, H&agrave; Nội (4.877 tỷ đồng)</strong></p>\r\n\r\n<p>Đ&acirc;y l&agrave; dự &aacute;n nằm s&aacute;t với khu đ&ocirc; thị sinh th&aacute;i Vinhomes Riverside, nơi Tập đo&agrave;n Vingroup đặt trụ sở ch&iacute;nh. Dự &aacute;n c&oacute; tổng diện t&i